import{_ as t,c as a,o as i,ae as s}from"./chunks/framework.BQlYxExx.js";const h=JSON.parse('{"title":"Backup, Recovery & Business Continuity","description":"","frontmatter":{},"headers":[],"relativePath":"en/BACKUP.md","filePath":"en/BACKUP.md","lastUpdated":1764098389000}'),o={name:"en/BACKUP.md"};function n(r,e,l,c,u,d){return i(),a("div",null,[...e[0]||(e[0]=[s('<h1 id="backup-recovery-business-continuity" tabindex="-1">Backup, Recovery &amp; Business Continuity <a class="header-anchor" href="#backup-recovery-business-continuity" aria-label="Permalink to &quot;Backup, Recovery &amp; Business Continuity&quot;">â€‹</a></h1><blockquote><p>ðŸ‡ªðŸ‡¸ <strong><a href="./../es/BACKUP.html">Leer esto en EspaÃ±ol</a></strong></p></blockquote><blockquote><p><strong>Executive Summary:</strong> This document outlines our 3-2-1 backup strategy and a formal Business Continuity Plan (BCP) for Severity 1 incidents. The plan is guided by our cultural principle of <strong>Stoic Resilience</strong>, ensuring a calm, orderly, and effective response during a crisis. A mandatory quarterly validation test guarantees our technical readiness and reinforces our antifragile mindset.</p><hr><p><strong>Last Reviewed:</strong> 2025-11-20 <strong>Document Owner:</strong> Head of Infrastructure &amp; SRE</p></blockquote><h2 id="_1-strategy-resilience-continuity" tabindex="-1">1. Strategy: Resilience &amp; Continuity <a class="header-anchor" href="#_1-strategy-resilience-continuity" aria-label="Permalink to &quot;1. Strategy: Resilience &amp; Continuity&quot;">â€‹</a></h2><ul><li><strong>Backup Strategy:</strong> We follow the 3-2-1 rule with automated, daily, off-site snapshots of our database, enabling Point-in-Time Recovery to within ~5 minutes of an incident.</li><li><strong>Business Continuity Plan (BCP):</strong> In a crisis, we activate a formal BCP led by an Incident Commander. Our response is guided by the principles in our <strong><a href="./../strategy/CULTURE_AND_LEADERSHIP.html">Antifragility &amp; Crisis Guide</a></strong>, emphasizing calm, decisive action.</li></ul><h2 id="_2-recovery-flow" tabindex="-1">2. Recovery Flow <a class="header-anchor" href="#_2-recovery-flow" aria-label="Permalink to &quot;2. Recovery Flow&quot;">â€‹</a></h2><ol><li><strong>Declaration:</strong> An incident is declared. The IC assembles the team.</li><li><strong>Assessment &amp; Communication:</strong> The impact is assessed. The Communications Lead begins updating stakeholders via our Statuspage.</li><li><strong>Execution:</strong> The Technical Lead restores the database to a <strong>new</strong> instance.</li><li><strong>Failover:</strong> After validation, traffic is redirected to the restored system.</li><li><strong>Post-Mortem:</strong> A blameless post-mortem is conducted to learn and improve.</li></ol><h2 id="_3-process-validation-checklist-quarterly" tabindex="-1">3. Process Validation Checklist (Quarterly) <a class="header-anchor" href="#_3-process-validation-checklist-quarterly" aria-label="Permalink to &quot;3. Process Validation Checklist (Quarterly)&quot;">â€‹</a></h2><ul><li><strong>[ ] Automated Restoration Test:</strong> A script (see <code>test-db-restore.sh</code>) is executed to prove we can restore the latest backup and that the data is valid.</li><li><strong>[ ] BCP Drill:</strong> A tabletop exercise simulating a major outage is conducted with the incident response team to ensure all members know their roles and communication protocols.</li></ul>',9)])])}const p=t(o,[["render",n]]);export{h as __pageData,p as default};
